mapper.select:
select * from ( SELECT NVL (A .PZ_NAME, '合计') pzName, SUM(ss.fkimg) as yield, SUM(A.planNum) as planNum, CASE WHEN SUM (ss.fkimg) / SUM (A.planNum) IS NULL THEN '0' ELSE TO_CHAR ( ROUND ( SUM (ss.fkimg) / SUM (A.planNum) * 100, 2 ) ) END AS schedule FROM ( SELECT PZ_NAME, sum ( CASE WHEN #{month} = '01' THEN JAN_MONTH WHEN #{month} = '02' THEN FEB_MONTH WHEN #{month} = '03' THEN MAR_MONTH WHEN #{month} = '04' THEN APR_MONTH WHEN #{month} = '05' THEN MAY_MONTH WHEN #{month} = '06' THEN JUN_MONTH WHEN #{month} = '07' THEN JUL_MONTH WHEN #{month} = '08' THEN AUG_MONTH WHEN #{month} = '09' THEN SEP_MONTH WHEN #{month} = '10' THEN OCT_MONTH WHEN #{month} = '11' THEN NOV_MONTH WHEN #{month} = '12' THEN DEC_MONTH END ) planNum, PZ_CODE FROM crm_resource_allocation WHERE 1=1
and YEAR = #{year}
and FL = #{flName}
group by PZ_NAME,PZ_CODE ) A LEFT JOIN ( SELECT svtpg.PRODUCT_GROUP pGroup, svtpg.VARIETY pz, sum(ssm.sl) fkimg FROM SCM_STEEL_SETTLE_MAIN ssm join SCM_SETTLEMENT_DETAILS ssd ON ssm.vbeln = ssd.vbeln JOIN scm_variety_to_pt_gp svtpg on ssd.pz_id = svtpg.id WHERE 1=1
and TO_CHAR (ssm.FKDAT, 'YYYY-MM') = #{date}
AND (ssd.PREY1 != 'X' or ssd.PREY1 is null) group by svtpg.PRODUCT_GROUP,svtpg.VARIETY ) ss ON A.PZ_CODE = ss.pGroup and A.PZ_NAME = ss.pz GROUP BY ROLLUP (A.PZ_NAME)) order by decode(pzName,'合计',1,'热板',2,'冷板',3,'宽厚板',4,'棒线',5,'型带',6)

select * from ( SELECT CX_NAME cxName, SUM(ss.fkimg) as yield, SUM(A.planNum) as planNum, CASE WHEN SUM (ss.fkimg) / SUM (A.planNum) IS NULL THEN '0' ELSE TO_CHAR ( ROUND ( SUM (ss.fkimg) / SUM (A.planNum) * 100, 2 ) ) END AS schedule FROM ( SELECT sum ( CASE WHEN #{month} = '01' THEN JAN_MONTH WHEN #{month} = '02' THEN FEB_MONTH WHEN #{month} = '03' THEN MAR_MONTH WHEN #{month} = '04' THEN APR_MONTH WHEN #{month} = '05' THEN MAY_MONTH WHEN #{month} = '06' THEN JUN_MONTH WHEN #{month} = '07' THEN JUL_MONTH WHEN #{month} = '08' THEN AUG_MONTH WHEN #{month} = '09' THEN SEP_MONTH WHEN #{month} = '10' THEN OCT_MONTH WHEN #{month} = '11' THEN NOV_MONTH WHEN #{month} = '12' THEN DEC_MONTH END ) planNum, CX_NAME FROM crm_resource_allocation WHERE 1=1
and YEAR = #{year}
and FL = #{flName}
group by CX_NAME ) A LEFT JOIN ( SELECT crd.full_name fName, crd.name dName, sum(ssm.sl) fkimg FROM SCM_STEEL_SETTLE_MAIN ssm join SCM_SETTLEMENT_DETAILS ssd ON ssm.vbeln = ssd.vbeln JOIN CRM_RESOURCE_DATA crd on ssd.cx_id = crd.code and crd.TYPE = 3 AND crd.parent_name IS NOT NULL WHERE 1=1
and TO_CHAR (ssm.FKDAT, 'YYYY-MM') = #{date}
group by crd.full_name,crd.name ) ss ON A.CX_NAME = ss.dName GROUP BY A.CX_NAME ORDER BY schedule ${sort} ) where rownum <= 10

select * from (SELECT NVL(A.companyName, '合计') companyName, SUM(ss.fkimg) as yield, SUM(A.planNum) as planNum FROM (SELECT sum ( CASE WHEN #{month} = '01' THEN JAN_MONTH WHEN #{month} = '02' THEN FEB_MONTH WHEN #{month} = '03' THEN MAR_MONTH WHEN #{month} = '04' THEN APR_MONTH WHEN #{month} = '05' THEN MAY_MONTH WHEN #{month} = '06' THEN JUN_MONTH WHEN #{month} = '07' THEN JUL_MONTH WHEN #{month} = '08' THEN AUG_MONTH WHEN #{month} = '09' THEN SEP_MONTH WHEN #{month} = '10' THEN OCT_MONTH WHEN #{month} = '11' THEN NOV_MONTH WHEN #{month} = '12' THEN DEC_MONTH END ) planNum, cra.CX_NAME, clcr.company_name companyName FROM crm_resource_allocation cra, crm_line_class_relation clcr WHERE 1=1
and YEAR = #{year}
and clcr.product_line in (select DISTINCT cx_name from crm_resource_allocation) group by cra.CX_NAME,clcr.company_name) A LEFT JOIN (SELECT data.NAME dName, data.full_name fName, SUM(settle.fkimg) fkimg FROM scm_steel_settle settle JOIN scm_delivery_detail detail ON settle.aubel = detail.order_no AND settle.aupos = detail.order_item JOIN crm_resource_data data on DATA.TYPE = 3 AND DATA.parent_name IS NOT NULL and data.full_name = detail.ext WHERE 1 = 1
and TO_CHAR (settle.FKDAT, 'YYYY-MM') = #{date}
AND (settle.PREY1 != 'X' or settle.PREY1 is null) GROUP BY DATA . NAME, DATA.full_name) ss ON A.CX_NAME = ss.dName OR A.CX_NAME = ss.fName GROUP BY ROLLUP(A.companyName)) order by decode(companyName,'合计',1,'唐钢',2,'邯钢',3,'宣钢',4,'承钢',5,'舞钢',6)

select * from ( SELECT a.flName flName, SUM(ss.fkimg) as yield, SUM(A.planNum) as planNum FROM (SELECT sum ( CASE WHEN #{month} = '01' THEN JAN_MONTH WHEN #{month} = '02' THEN FEB_MONTH WHEN #{month} = '03' THEN MAR_MONTH WHEN #{month} = '04' THEN APR_MONTH WHEN #{month} = '05' THEN MAY_MONTH WHEN #{month} = '06' THEN JUN_MONTH WHEN #{month} = '07' THEN JUL_MONTH WHEN #{month} = '08' THEN AUG_MONTH WHEN #{month} = '09' THEN SEP_MONTH WHEN #{month} = '10' THEN OCT_MONTH WHEN #{month} = '11' THEN NOV_MONTH WHEN #{month} = '12' THEN DEC_MONTH END ) planNum, cra.CX_NAME, cra.fl_name flName FROM crm_resource_allocation cra, crm_line_class_relation clcr WHERE 1 = 1
and cra.YEAR = #{year}
and clcr.product_line in (select DISTINCT cx_name from crm_resource_allocation)
and clcr.company_name = #{companyName}
group by cra.CX_NAME,cra.fl_name) A LEFT JOIN (SELECT data.NAME dName, data.full_name fName, SUM(settle.fkimg) fkimg FROM scm_steel_settle settle JOIN scm_delivery_detail detail ON settle.aubel = detail.order_no AND settle.aupos = detail.order_item JOIN crm_resource_data data on DATA.TYPE = 3 AND DATA.parent_name IS NOT NULL and data.full_name = detail.ext WHERE 1 = 1
and TO_CHAR (settle.FKDAT, 'YYYY-MM') = #{date}
AND (settle.PREY1 != 'X' or settle.PREY1 is null) GROUP BY DATA . NAME, DATA.full_name) ss ON A.CX_NAME = ss.dName OR A.CX_NAME = ss.fName GROUP BY a.flName) order by decode(flName,'销售总公司',1,'技术中心、事业部',2,'出口',3,'现货',4,'子公司其他',5,'品种钢',6)
