mapper.select:
select * from (select '1' yf,year || '1月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(JAN_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '2' yf,year || '2月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(FEB_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '3' yf,year || '3月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(MAR_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '4' yf,year || '4月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(APR_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '5' yf,year || '5月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(MAY_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '6' yf,year || '6月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(JUN_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '7' yf,year || '7月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(JUL_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '8' yf,year || '8月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(AUG_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '9' yf,year || '9月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(SEP_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '10' yf,year || '10月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(OCT_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '11' yf,year || '11月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(NOV_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name union all select '12' yf,year || '12月' rq,PZ_NAME pz, cx_name cx, fl_name, sum(DEC_MONTH) jhl from crm_resource_allocation where STATUS = '0' group by year, PZ_NAME, cx_name, fl_name) a
where rq like '%'||#{nf}||'%'
and yf = #{yf}
and pz = #{pz}
and cx = #{cx}
and fl_name = #{xszt}
ORDER BY fl_name , cx , "DECODE"(pz,'热板','冷板','宽厚板','棒线','型材')

select distinct cx_name cx
from crm_resource_allocation

    where pz_name = #{pz}
